package com.breezinghealth.transation;import java.util.ArrayList;import android.content.ContentProviderOperation;import android.util.Log;import com.breezinghealth.providers.Breezing.HeatIngestion;/*** * 所有与批处理数据插入相关部分都放到这个类下面 * * */public class DataBatchInserter {    private static String TAG = "DataBatchInserter";}class HeatIngestionInserter extends DataRowModify {    public HeatIngestionInserter() {    }    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " HeatIngestionInserter buildOperation");        ops.add(ContentProviderOperation.newInsert(HeatIngestion.CONTENT_URI)                .withValue(HeatIngestion.FOOD_TYPE, RowData[0])                .withValue(HeatIngestion.FOOD_NAME, RowData[1])                .withValue(HeatIngestion.NAME_EXPRESS, RowData[2])                .withValue(HeatIngestion.PRIORITY, RowData[3])                .withValue(HeatIngestion.FOOD_SIZE, RowData[4])                .withValue(HeatIngestion.CALORIE, RowData[5])                .build());    }}