package com.breezinghealth.transation;import com.breezinghealth.providers.Breezing.HeatIngestion;import android.content.ContentProviderOperation;import android.util.Log;import java.util.ArrayList;public class DataBatchUpdater {}class HeatIngestionUpdater extends DataRowModify {    @Override    public void buildOperation(String[] RowData, ArrayList<ContentProviderOperation> ops) {        Log.d(DataTaskService.TAG, " HeatIngestionUpdater buildOperation");        if (RowData == null ) {            ops.add(ContentProviderOperation.newDelete(HeatIngestion.CONTENT_URI)                    .build());        } else {            ops.add(ContentProviderOperation.newInsert(HeatIngestion.CONTENT_URI)                    .withValue(HeatIngestion.FOOD_TYPE, RowData[0])                    .withValue(HeatIngestion.FOOD_NAME, RowData[1])                    .withValue(HeatIngestion.NAME_EXPRESS, RowData[2])                    .withValue(HeatIngestion.PRIORITY, RowData[3])                    .withValue(HeatIngestion.FOOD_SIZE, RowData[4])                    .withValue(HeatIngestion.CALORIE, RowData[5])                    .build());        }    }}